INSERT INTO `users_useradd_model` (`id`, `cname`, `dept`, `description`, `website`, `method`, `record`, `attackresult`, `uregid_id`) VALUES
(1, 'Administration of Radioactive Substances Advisory Committee', 'Department of Health', 'To advise Health Ministers with respect to the grant, renewal, suspension, revocation and variation of certificates and generally in connection with the system of prior authorisation required by Article 5(a) of Council Directive 76/579/Euratom. This has been superseded by the Basic Safety Standard Directive for radiation protection 059/2013 but not yet implemented into UK law.', 'http://www.gov.uk/arsac', 'hacked', '2,200,000', 'Macro viruses', 1),
(2, 'Advisory Committee on Animal Feeding stuffs', 'Food Standards Agency', 'Advises on the safety and use of animal feeds and feeding practices, with particular emphasis on protecting human health, and with reference to new technical developments.', 'http://acaf.food.gov.uk/', 'poor security', '175,350', 'File infectors', 1),
(3, 'Advisory Committee on Business Appointments', 'Cabinet Office', 'The Committee provides independent advice when a senior Crown servant or former Minister leaves office and wishes to take up an outside appointment or employment.', 'http://acoba.independent.gov.uk/', 'hacked', '152,000,000', 'System or boot-record infectors', 1),
(4, 'Advisory Committee on Clinical Excellence Awards', 'Department of Health', 'ACCEA administers the National Clinical Excellence Awards Scheme. It advises Ministers on which consultants should receive new National Awards and whether existing award holders have provided satisfactory evidence for renewal.', 'https://www.gov.uk/government/organisations/advisory-committee-on-clinical-excellence-awards', 'lost / stolen media', '4,000,000', 'Polymorphic viruses', 1),
(5, 'Advisory Committee on Conscientious Objectors', 'Ministry of Defence', 'The Committee is responsible for advising the Secretary of State for Defence on all claims to conscientious objection to further service in the Armed Forces made by officers and other ranks of the Armed Forces whose application for permission to retire or resign their commissions or for discharge have been refused by the Service authorities.', 'https://www.gov.uk/government/organisations/advisory-committee-on-conscientious-objectors', 'hacked', '75,000', 'Ransomware', 1),
(6, 'Advisory Committee on Justices of the Peace (x44)', 'Ministry of Justice', 'To recruit, select and recommend to the Lord Chief Justice candidates with the necessary qualities for appointment as Justices of the Peace in England and Wales.', '', 'lost / stolen media', '344,579', 'Logic bombs', 1),
(7, 'Advisory Committee on Novel Foods and Processes', 'Food Standards Agency', 'Advises the central authorities responsible in England, Scotland, Wales and Northern Ireland on any matters relating to novel foods and novel food processes, including food irradiation and GMOs', 'http://acnfp.food.gov.uk/', 'lost / stolen media', '200,000', 'Worms', 1),
(8, 'Advisory Committee on Releases to the Environment', 'Department for Environment, Food and Rural Affairs', 'To advise government on the risks to human health and the environment from the release of genetically modified organisms.', 'https://www.gov.uk/government/organisations/advisory-committee-on-releases-to-the-environment', 'poor security', '300,000', 'Droppers', 1),
(9, 'Advisory Committee on the Microbiological Safety of Food', 'Food Standards Agency', 'Provides expert advice to Government on questions relating to microbiological issues and food.', 'http://acmsf.food.gov.uk/', 'hacked', '156,000', 'Spyware', 1),
(10, 'Advisory Council on National Records and Archives', 'National Archives, The', 'To advise the Secretary of State for Digital, Culture, Media and Sport on issues relating to access to public records and to represent the public interest in deciding what records should be open or closed', 'http://www.nationalarchives.gov.uk/about/our-role/advisory-council/', 'hacked', '80,000,000', 'Macro viruses', 1),
(11, 'Advisory Council on the Misuse of Drugs (ACMD)', 'Home Office', 'Advisory, statutory, NDPB as constituted under the Misuse of Drugs Act 1971.', 'https://www.gov.uk/government/organisations/advisory-council-on-the-misuse-of-drugs', 'inside job, hacked', '92,000,000', 'File infectors', 1),
(12, 'Advisory, Conciliation and Arbitration Service', 'Department for Business, Energy and Industrial Strategy', 'ACAS provides information, advice, training, conciliation and other services for employers and employees to help prevent or resolve workplace problems.', 'http://www.acas.org.uk/', 'accidentally published', '20,000,000', 'System or boot-record infectors', 1),
(13, 'Agriculture and Horticulture Development Board', 'Department for Environment, Food and Rural Affairs', 'Functions defined in AHDB Order 2008. Funded by agriculture industry through statutory levies with the purpose to improve the competitiveness and sustainability of the industries which fund it.', 'http://www.ahdb.org.uk/', 'hacked', '2,400,000', 'Polymorphic viruses', 1),
(14, 'Animal and Plant Health Agency', 'Department for Environment, Food and Rural Affairs', 'To safeguard animal and plant health for the benefit of people, the environment and the economy.', 'http://www.defra.gov.uk/apha-en/', 'hacked', '275,000', 'Ransomware', 1),
(15, 'Animals in Science Committee (ASC)', 'Home Office', 'To advise the Secretaries of State for the Home Office and Northern Ireland Office on their duties under the terms of the Animals (Scientific Procedures) Act 1986 as amended. It is also responsible for exchanging information with similar EU National Committees in other member states and sharing best practice within the EU on the operation of animal welfare bodies.', 'https://www.gov.uk/government/organisations/animals-in-science-committee', 'poor security', '91,000', 'Logic bombs', 1),
(16, 'Armed Forces Pay Review Body', 'Ministry of Defence', 'The Armed Forces Pay Review Body is to provide independent advice to the Prime Minister and the Secretary of State for Defence on the remuneration and charges for members of the Naval, Military and Air Forces of the Crown.', 'https://www.gov.uk/government/organisations/armed-forces-pay-review-body', 'accidentally published', '12,367,232', 'Worms', 1),
(17, 'Arts and Humanities Research Council', 'Department for Business, Energy and Industrial Strategy', 'The AHRC promotes and supports research and postgraduate training in the arts and humanities.  To encourage the wider application of, and raise the profile of, arts and humanities research.', 'http://www.ahrc.ac.uk/', 'hacked', '32,000,000', 'Droppers', 1),
(18, 'Arts Council England', 'Department for Culture, Media and Sport', 'The national funding body for the arts, museums and libraries in England. It is responsible for developing and improving the knowledge, understanding and practise of the arts and to increase the accessibility of the arts to the public through the distribution of exchequer money from central government and revenue from the National Lottery.', 'http://www.artscouncil.org.uk/', 'lost / stolen computer', '113,000', 'Spyware', 1),
(19, 'Big Lottery Fund', 'Department for Culture Media and Sport', 'The Big Lottery Fund (BLF) distributes its 40% share of National Lottery good causes money to bring real improvements to communities and the lives of people most in need. While sponsored by Cabinet Office, BLF is also financially accountable to Department for Culture, Media and Sport for management of its lottery money.', 'http://www.biglotteryfund.org.uk/', 'hacked', '114,000', 'Macro viruses', 1),
(20, 'Biotechnology and Biological Sciences Research Council', 'Department for Business, Energy and Industrial Strategy', 'The Biotechnology and Biological Sciences Research Council is the UK agency for investing in research in the life sciences.  BBSRC carries out its mission by investing  research, providing training in the biosciences and fostering opportunities for knowledge to deliver growth and jobs.', 'http://www.bbsrc.ac.uk/', 'hacked', '18,000,000', 'File infectors', 1),
(21, 'Board of Trustees of the Royal Botanic Gardens Kew', 'Department for Environment, Food and Rural Affairs', 'To be the global resource for plant and fungal knowledge, building an understanding of the world''s plants and fungi upon which all our lives depend.', 'http://www.kew.org/about-kew/index.htm', 'accidentally published', 'G20 world leaders', 'System or boot-record infectors', 1),
(22, 'Boundary Commission for England', 'Cabinet Office', 'The Commission’s statutory function is to keep under review the distribution of constituencies in England and to make reports with recommendations every 5 years in accordance with the provisions of the Parliamentary Constituencies Act 1986 (as amended).', 'http://boundarycommissionforengland.independent.gov.uk/', 'poor security', '125,000', 'Polymorphic viruses', 1),
(23, 'Boundary Commission for Northern Ireland', 'Northern Ireland Office', 'The Commission''s role is to keep under continuous review the number, names and boundaries of the parliamentary constituencies into which Northern Ireland is divided and to make recommendations about these to the Secretary of State for Northern Ireland.', 'http://www.boundarycommission.org.uk/', 'lost / stolen computer', '1,220,000', 'Ransomware', 1),
(24, 'Boundary Commission for Scotland', 'Scotland Office', 'To  review the boundaries of Westminster Parliamentary Constituencies in Scotland', 'http://www.bcomm-scotland.independent.gov.uk/', 'hacked', '250,000', 'Logic bombs', 1),
(25, 'Boundary Commission for Wales', 'Cabinet Office', 'The Commission’s statutory function is to keep under review the distribution of constituencies in Wales and to make reports with recommendations every 5 years in accordance with the provisions of the Parliamentary Constituencies Act 1986 (as amended).', 'http://bcomm-wales.gov.uk', 'lost / stolen media', '1,200,000', 'Worms', 1),
(26, 'British Council', 'Foreign and Commonwealth Office', '', 'http://www.britishcouncil.org/', 'hacked', '63 stores', 'Droppers', 1),
(27, 'British Film Institute', 'Department for Culture, Media and Sport', 'The British Film Institute (BFI) is the lead organisation for film in the UK.  It uses Lottery funds to support film production, distribution, education and audience development. Since 1933 it has cared for the BFI National Archive, and celebrated the best of British and international film-making through festivals, film restoration, DVD releases and cinema programming.', 'http://www.bfi.org.uk/', 'unknown', 'unknown', 'Spyware', 1),
(28, 'British Hallmarking Council', 'Department for Business, Energy and Industrial Strategy', 'The British Hallmarking Council supervises UK hallmarking.', 'https://www.gov.uk/government/organisations/british-hallmarking-council', 'poor security', '1,900,000', 'Macro viruses', 1),
(29, 'British Library', 'Department for Culture, Media and Sport', 'The National Library of the UK.  The British Library Board is responsible for managing the library as a national centre for reference, study, bibliographical and information services, in relation to both scientific and technological matters and the humanities.', 'http://www.bl.uk/', 'hacked', '100,000', 'File infectors', 1),
(30, 'British Museum', 'Department for Culture, Media and Sport', 'The British Museum holds for the benefit of humanity a collection representative of world cultures that is housed in safety, conserved, curated, researched and exhibited.', 'http://www.britishmuseum.org/', 'hacked', '2,300,000', 'System or boot-record infectors', 1),
(31, 'British Pharmacopoeia Commission', 'Department of Health', 'To publish any new editions of and/or amendments to the British Pharmacopoeia and British Pharmacopoeia (Veterinary), together with the establishment and publication of British Approved Names.', 'www.pharmacopoeia.com', 'hacked', '200,000', 'Polymorphic viruses', 1),
(32, 'British Transport Police Authority (BTPA)', 'Department for Transport', 'The Authority is the independent body responsible for ensuring an efficient and effective British Transport Police (BTP) force for rail operators, their staff and passengers.', 'http://btpa.police.uk/', 'accidentally published', '', 'Ransomware', 1),
(33, 'Building Regulations Advisory Committee', 'Department for Communities and Local Government', 'To advise the Secretary of State on the exercise of his powers to make building regulations for England and on other subjects connected with building regulations. Operating guidelines, and the responsibilities of the Secretariat, Chair and members are set out in the BRAC handbook.', 'https://www.gov.uk/government/organisations/building-regulations-advisory-committee', 'hacked', '7,633,234', 'Logic bombs', 1),
(34, 'Care Quality Commission', 'Department of Health', 'The purpose of Care Quality Commission is to make sure health and social care services provide people with safe, effective, compassionate, high quality care and to encourage care services to improve.  Its role is to monitor, inspect and regulate services to make sure they meet fundamental standards of quality and safety and to publish what it finds, including performance ratings to help people choose care.', 'http://www.cqc.org.uk/', 'hacked', '14,000,000', 'Worms', 1),
(35, 'Central Arbitration Committee', 'Department for Business, Energy and Industrial Strategy', 'The CAC encourages fair and efficient arrangements in the workplace by resolving collective disputes in England, Scotland and Wales, either by voluntary agreement or, if necessary, through a legal decision. It is funded through Acas.', 'http://www.cac.gov.uk/', 'lost / stolen media', '1,023,209', 'Droppers', 1),
(36, 'Centre for Environment, Fisheries and Aquaculture Science', 'Department for Environment, Food and Rural Affairs', 'To make a real difference for society by supporting the long-term prosperity and well-being of industries, communities and individuals that enjoy and depend on the rich natural assets found in our marine and freshwater environments.', 'https://www.gov.uk/government/organisations/centre-for-environment-fisheries-and-aquaculture-science', 'poor security', '90,000', 'Spyware', 1),
(37, 'Charity Commission for England and Wales', 'Charity Commission for England and Wales', 'The Charity Commission is the independent Government department which registers and regulates charities in England and Wales, as set out in the Charities Act 2011.', 'https://www.gov.uk/government/organisations/charity-commission', 'hacked', '380,000', 'Macro viruses', 1),
(38, 'Children and Family Court Advisory and Support Service', 'Ministry of Justice', 'Cafcass looks after the interests of children involved in family court proceedings.  They work with children and their families, and advise the courts on the best interests of individual children.', 'http://www.cafcass.gov.uk/', 'hacked', 'tens of thousands', 'File infectors', 1),
(39, 'Civil Justice Council', 'Ministry of Justice', 'The Civil Justice Council has a statutory duty under the Civil Procedure Act 1997 to keep the civil justice system under review; consider how to make the civil justice system more accessible, fair and efficient; advise the Lord Chancellor and the Judiciary on the development of the civil justice system; refer proposals for change in the civil justice system to the Lord Chancellor and the Civil Procedure Committee; and make proposals for research.', 'http://www.judiciary.gov.uk/about-the-judiciary/advisory-bodies/cjc', 'lost / stolen media', '800,000', 'System or boot-record infectors', 1),
(40, 'Civil Nuclear Police Authority', 'Department for Business, Energy and Industrial Strategy', 'The CNPA oversees the Civil Nuclear Constabulary (CNC) and must ensure that their policing meets the need of the nuclear operating companies', 'https://www.gov.uk/government/organisations/civil-nuclear-police-authority', 'hacked', '40,000,000', 'Polymorphic viruses', 1),
(41, 'Civil Procedure Rule Committee', 'Ministry of Justice', 'The Civil Procedure Rule Committee was created by virtue of Section 2 of the Civil Procedure Act 1997 (as amended by section 83 of the Courts Act 2003) to make rules of court for the Civil Division of the Court of Appeal, the High Court and the county courts.', 'https://www.gov.uk/government/organisations/civil-procedure-rules-committee', '', '', 'Ransomware', 1),
(42, 'Civil Service Commission', 'Cabinet Office', 'Regulates recruitment to the Civil Service', 'http://civilservicecommission.independent.gov.uk/', 'hacked', '1,100,000', 'Logic bombs', 1),
(43, 'Coal Authority', 'Department for Business, Energy and Industrial Strategy', 'The Coal Authority manages the effects of past coal mining, including subsidence damage claims which are not the responsibility of licensed coal mine operators.', 'https://www.gov.uk/government/organisations/the-coal-authority', 'hacked', '9,400,000', 'Worms', 1),
(44, 'Commission on Human Medicines', 'Department of Health', 'To advise the Health Ministers and the Licensing Authority (LA) on matters relating to human medicinal products, and to consider those applications that lead to LA action as appropriate (i.e. where the LA has a statutory duty to refer or chooses to do so).', 'https://www.gov.uk/government/organisations/commission-on-human-medicines', 'hacked', '75,000', 'Droppers', 1),
(45, 'Commissioners for the Reduction of the National Debt', 'Commissioners for the Reduction of the National Debt', 'The Commissioners for the Reduction of the National Debt (CRND)''s main function is the investment and management of government funds, including the National Insurance Fund Investment Account, the National Lottery Distribution Fund Investment Account and the Court Funds Investment Account.', 'http://www.dmo.gov.uk/index.aspx?page=CRND/CRND_Index', 'hacked', '60,000', 'Spyware', 1),
(46, 'Committee on Climate Change', 'Department for Business, Energy and Industrial Strategy', 'The CCC was established under the Climate Change Act 2008 to advise the UK Government and Devolved Administrations on emissions targets, and report to Parliament on progress made in reducing greenhouse gas emissions and preparing for climate change.', 'http://www.theccc.org.uk/', 'hacked', '110,000', 'Macro viruses', 1),
(47, 'Committee on Fuel Poverty', 'Department for Business, Energy and Industrial Strategy', 'The CFP advises on the effectiveness of policies aimed at reducing fuel poverty, and encourages greater co-ordination across  organisations working to reduce fuel poverty.', 'https://www.gov.uk/government/organisations/committee-on-fuel-poverty', 'hacked', '5,000,000', 'File infectors', 1),
(48, 'Committee on Mutagenicity of Chemicals in Food, Consumer Products and the Environment', 'Department of Health', 'To assess and advise on the mutagenic risk to man of substances in food, consumer products and the environment.', 'https://www.gov.uk/government/organisations/committee-on-mutagenicity-of-chemicals-in-food-consumer-products-and-the-environment', 'hacked', '6,000,000', 'System or boot-record infectors', 1),
(49, 'Committee on Radioactive Waste Management', 'Department for Business, Energy and Industrial Strategy', 'CoRWM provides independent scrutiny and advice to the UK governments on the long-term management of higher activity radioactive wastes.', 'http://www.gov.uk/government/organisations/committee-on-radioactive-waste-management', 'hacked', '10,000,000', 'Polymorphic viruses', 1),
(50, 'Committee on Standards in Public Life', 'Cabinet Office', 'The Committee examines standards of ethical conduct among public office holders in the UK and makes recommendations to the government as to any changes in present arrangements, including those relating to party funding.', 'http://www.public-standards.gov.uk/', 'lost / stolen media', '3,900,000', 'Ransomware', 1),
(51, 'Committee on Toxicity of Chemicals in Food, Consumer Products and the Environment', 'Food Standards Agency', 'Advises the FSA, the DoH and other Government Departments and Agencies on matters concerning the toxicity of chemicals.', 'http://cot.food.gov.uk/', 'hacked', '360,083', 'Logic bombs', 1),
(52, 'Commonwealth Scholarship Commission in the UK', 'Department for International Development', 'Responsible for managing Britain’s contribution to the Commonwealth Scholarship and Fellowship Plan; an international programme under which member governments offer scholarships and fellowships to citizens of other Commonwealth countries.', 'http://cscuk.dfid.gov.uk/', 'poor security', '150,000', 'Worms', 1),
(53, 'Companies House', 'Department for Business, Energy and Industrial Strategy', 'Incorporates and dissolves limited companies, registers the information companies are legally required to supply, and makes that information available to the public.', 'https://www.gov.uk/government/organisations/companies-house', 'lost / stolen media', '160,000', 'Droppers', 1),
(54, 'Competition and Markets Authority', 'Competition and Markets Authority', 'The CMA works to promote competition for the benefit of consumers, both within and outside the UK. Its aim is to make markets work well for consumers, businesses and the economy.', 'http://www.gov.uk/cma', 'lost / stolen computer', '105,470', 'Spyware', 1),
(55, 'Competition Appeals Tribunal', 'Department for Business, Energy and Industrial Strategy', 'The Competition Appeal Tribunal is a specialist judicial body with cross-disciplinary expertise in law, economics, business and accountancy whose function is to hear and decide cases involving competition or economic regulatory issues.', 'http://www.catribunal.org.uk/', 'hacked', '4,500,000', 'Macro viruses', 1),
(56, 'Competition Service', 'Department for Business, Energy and Industrial Strategy', 'The CS funds and provides support services to the Competition Appeal Tribunal.', 'http://www.catribunal.org.uk/', 'inside job', '1,000,000', 'File infectors', 1),
(57, 'Construction Industry Training Board', 'Department for Education', 'To ensure the quantity and quality of training provision is adequate to meet the current and future skills needs of the industry.', 'http://www.citb.co.uk/', 'inside job', '2,600,000', 'System or boot-record infectors', 1),
(58, 'Consumer Council for Water', 'Department for Environment, Food and Rural Affairs', 'To represent consumers of water and sewerage services in England & Wales', 'http://www.ccwater.org.uk/', 'inside job', '2,500,000', 'Polymorphic viruses', 1),
(59, 'Copyright Tribunal', 'Department for Business, Energy and Industrial Strategy', 'The Copyright Tribunal’s primary purpose is to resolve commercial licensing disputes between copyright owners or their agents (collecting societies) and people who use copyrighted material in their business.  It operates as a cost centre within IPO.', 'https://www.gov.uk/government/organisations/copyright-tribunal', 'hacked', '40,000', 'Ransomware', 1),
(60, 'Criminal Cases Review Commission', 'Ministry of Justice', 'To review possible miscarriages of justice in the criminal courts of England, Wales and to refer appropriate cases to the appeal courts.', 'www.ccrc.gov.uk', 'lost / stolen computer', '100,000', 'Logic bombs', 1),
(61, 'Criminal Injuries Compensation Authority', 'Ministry of Justice', 'A government organisation that can pay money (compensation) to people who have been physically or mentally injured because they were the blameless victim of a violent crime.', 'https://www.gov.uk/government/organisations/criminal-injuries-compensation-authority', 'hacked', 'millions', 'Worms', 1),
(62, 'Criminal Procedure Rule Committee', 'Ministry of Justice', 'To streamline and modernise the practice and procedure to be followed in the criminal trials of England and Wales (section 69 of the Courts Act 2003 refers.) The membership reflects the key participants in the conduct of a criminal case.  The Criminal Procedure Rule Committee consults widely before making new rules.', 'https://www.gov.uk/government/organisations/criminal-procedure-rule-committee', 'inside job', '8,637,405', 'Droppers', 1),
(63, 'Crown Commercial Service, The', 'Cabinet Office', 'The Crown Commercial Service brings together policy, advice and direct buying: providing commercial services to the public sector and saving money for the taxpayer.', 'https://www.gov.uk/government/organisations/crown-commercial-service', 'hacked', '8,000,000', 'Spyware', 1),
(64, 'Crown Prosecution Service', 'Crown Prosecution Service', 'The Crown Prosecution Service (CPS) is an independent body set up in 1986 to prosecute criminal cases in England and Wales. The CPS works closely with the police and other investigators to advise on lines of inquiry and to decide on appropriate charges or other disposals in all but minor cases. CPS prosecutors prepare cases for court and present cases in both the magistrates’ courts and, increasingly, in the higher courts. The Director of Public Prosecutions is the head of the CPS and operates independently, under the superintendence of the Attorney General who is accountable to Parliament for the work of the CPS.', 'http://www.cps.gov.uk/', '', '', 'Macro viruses', 1),
(65, 'Defence Electronics and Components Agency', 'Ministry of Defence', 'Its role is to provide through-life electronics and components maintenance, repair  and overhaul (MRO), upgrade, and procurement services for Defence equipment.  This includes the provision of an avionics obsolescence management capability and deployable services for a diverse range of electronic and mechanical equipment and components', 'https://www.gov.uk/government/organisations/defence-electronics-and-components-agency', 'hacked', '235 GB', 'File infectors', 1),
(66, 'Defence Nuclear Safety Committee', 'Ministry of Defence', 'The Defence Nuclear Safety Committee is responsible for providing the Secretary of State for Defence with an independent source of expertise and advice concerning the safety of Ministry of Defence nuclear programmes, facilities and operations.', 'https://www.gov.uk/government/organisations/defence-nuclear-safety-committee', 'poor security', '', 'System or boot-record infectors', 1),
(67, 'Defence Science and Technology Laboratory', 'Ministry of Defence', 'To maximise the impact of science and technology for the defence and security of the UK.', 'https://www.dstl.gov.uk/', '', '19,252', 'Polymorphic viruses', 1),
(68, 'Directly Operated Railways Ltd', 'Department for Transport', 'Directly Operated Railways Limited (DOR) managed the East Coast rail franchise until it was re-let to a new private operator in March 2015. The company is currently being wound down.', '', 'hacked', '600,000', 'Ransomware', 1),
(69, 'Disabled People''s Employment Corporation (GB) Ltd', 'Department for Work and Pensions', 'Following sale of business on 7th April 2015, Remploy Ltd renamed.  Continuing as ALB to manage out legacy issues of old company. Aiming to place in Members'' Voluntary Liquidation (MVL) by late summer 2017.', 'dpecgb.co.uk', 'hacked', 'unknown', 'System or boot-record infectors', 1),
(70, 'Disclosure and Barring Service', 'Home Office', 'Provides criminal records checks and barring decisions.', 'https://www.gov.uk/government/organisations/disclosure-and-barring-service', 'hacked', '1,000,000', 'Polymorphic viruses', 1),
(71, 'Driver and Vehicle Licensing Agency (DVLA)', 'Department for Transport', 'The DVLA ensures that complete and accurate registers of drivers and vehicles are held and makes them as accessible and as flexible as possible to those who have the right to use them. These registers underpin action by DVLA, the police and others to maintain the high levels of road safety experienced in the UK and the effective collection of vehicle excise duty (VED). DVLA''s regulatory activities relate to work on drivers medicals which represents a small part of the business.', 'www.gov.uk/dvla', 'hacked', '1,400,000', 'Ransomware', 1),
(72, 'Driver and Vehicle Standards Agency (DVSA)', 'Department for Transport', 'DVSA improves road safety in Great Britain by setting standards for driving and motorcycling, and making drivers, vehicle operators and MOT garages understand and follow roadworthiness standards. We also provide a range of licensing, testing, education and enforcement services.', 'www.gov.uk/dvsa', 'hacked', '1,000,000', 'Logic bombs', 1),
(73, 'Ebbsfleet Development Corporation', 'Department for Communities and Local Government', 'To coordinate investment and drive forward development of a new garden city at Ebbsfleet, building up to 15,000 new homes on mainly brownfield land.', 'http://www.ebbsfleetdc.org.uk/', 'hacked', '145,000,000', 'Worms', 1),
(74, 'Economic and Social Research Council', 'Department for Business, Energy and Industrial Strategy', 'The ESRC funds research into the big social and economic questions facing society today.  ESRC also develops and trains the UK''s future social scientists.  ESRC''s research informs public policies and helps make businesses, voluntary bodies and other organisations more effective.', 'http://www.esrc.ac.uk/', 'lost / stolen media', '3,300,000', 'Droppers', 1),
(75, 'Education Funding Agency', 'Department for Education', 'The Education Funding Agency is an executive agency of the Department for Education, responsible for the funding of education and training for children and young people between the ages of 3 and 19, and up to age 25 in the case of high needs students.  The EFA funds academy trusts and a range of post 16 providers directly and also allocates funding to local authorities for them to fund maintained schools.  In the case of academy trusts, the EFA has additional regulatory functions, e.g. on matters such as complaints.  The EFA is also responsible for a range of building and maintenance programmes for schools, academies, free schools and sixth-form colleges.', 'www.gov.uk/efa', 'lost / stolen computer', '514,330', 'Spyware', 1),
(76, 'Engineering and Physical Sciences Research Council', 'Department for Business, Energy and Industrial Strategy', 'The EPSRC funds research and training in engineering and the physical sciences.', 'http://www.epsrc.ac.uk/', 'inside job', '251,000', 'Macro viruses', 1),
(77, 'Engineering Construction Industry Training Board', 'Department for Education', 'To ensure the quantity and quality of training provision is adequate to meet the current and future skills needs of the industry.', 'http://www.ecitb.org.uk/', 'lost / stolen media', '180,111', 'File infectors', 1),
(78, 'Environment Agency', 'Department for Environment, Food and Rural Affairs', 'The Environment Agency is an Executive NDPB responsible to the Secretary of State for Environment, Food and Rural Affairs. Its principal aims are to protect and improve the environment and to promote sustainable development. It plays a central role in delivering the environmental priorities of Central Government including Flood and Coastal Erosion Risk Management, Climate Change Adaptation and Mitigation, Water Quality and Waste Management.', 'https://www.gov.uk/government/organisations/environment-agency', 'poor security', '315,000', 'System or boot-record infectors', 1),
(79, 'Equality and Human Rights Commission', 'Department for Education', 'The Commission is an independent non-partisan body that uses its powers to support and promote laws and practices that help make our society fair for everyone. The Commission helps to safeguard and enforce the laws that protect all our rights to fairness, dignity and respect. We contribute to making and keeping Britain a fair society in which everyone, regardless of background, has an equal opportunity to fulfil their potential. As a UN-accredited National Human Rights Institution and EU National Equality Body, we work with others in Scotland and Northern Ireland, across Europe and internationally to protect and promote equality and human rights.', 'http://www.equalityhumanrights.com/', 'poor security', '143,000,000', 'Polymorphic viruses', 1),
(80, 'Family Justice Council', 'Ministry of Justice', 'The Family Justice Council (FJC) aims to facilitate the delivery of better and quicker outcomes for families and children who use the family justice system. The Council''s primary role is to promote an inter-disciplinary approach to family justice, and through consultation and research, to monitor how effectively the system both as a whole and through its component parts delivers the service the Government and the public need and to advise on reforms necessary for continuous improvement. It is chaired by the President of the Family Division and under its revised terms of reference provides expert advice directly to the Family Justice Board and Government on the operation and reform of the Family Justice System.', 'http://www.judiciary.gov.uk/about-the-judiciary/advisory-bodies/fjc', 'poor security', 'unknown', 'Ransomware', 1),
(81, 'Family Procedure Rule Committee', 'Ministry of Justice', 'The Family Procedure Rule Committee will seek to produce a single, coherent and simply expressed set of rules governing practice and procedure in family proceedings in the High Court and family court. Before making such rules the committee will consult as it thinks appropriate.', 'http://www.justice.gov.uk/about/moj/advisory-groups/family-procedure-rule-committee', 'hacked', 'unknown', 'System or boot-record infectors', 1),
(82, 'FCO Services', 'Foreign and Commonwealth Office', 'A Trading Fund which provides a range of integrated secure services worldwide to the FCO, other UK public bodies and foreign governments and international organisations closely linked to the UK.', 'http://www.fcoservices.gov.uk/', 'hacked', '50,000,000', 'Polymorphic viruses', 1),
(83, 'Film Industry Training Board', 'Department for Education', 'To ensure the quantity and quality of training provision is adequate to meet the current and future skills needs of the film industry.', 'www.creativeskillset.org', 'hacked', '10,000,000', 'Ransomware', 1),
(84, 'Food Standards Agency', 'Food Standards Agency', 'The Food Standards Agency''s main aim is to protect people''s health and the interest of the consumer in relation to food.  The FSA is responsible for food safety and hygiene, working with businesses to help them produce safe food and with local authorities to enforce food safety regulations.', 'http://www.food.gov.uk/', 'hacked', '15,000,000', 'Logic bombs', 1),
(85, 'Forest Enterprise England', 'Forestry Commission', 'Forest Enterprise England manages the public forest estate, owned by the Secretary of State for Environment, Food and Rural Affairs in a sustainable way, for the benefit of people, the economy and the environment.', 'http://www.forestry.gov.uk/england', 'lost / stolen computer', 'unknown', 'Worms', 1),
(86, 'Forest Research', 'Forestry Commission', 'The Research Agency of the Forestry Commission provides research services relevant to UK and international forestry interests and inform and support forestry''s contribution to the UK government''s policies. Core role is to provide the evidence base for UK forestry practices and to support innovation.', 'http://www.forestry.gov.uk/forestresearch', 'poor security', '50,000,000', 'Droppers', 1),
(87, 'Forestry Commission', 'Forestry Commission', 'The Forestry Commission is a Non-Ministerial Department responsible for the delivery of forestry policy and management of the public forest estate in England.', 'http://www.forestry.gov.uk/', 'accidentally published', '6,000,000', 'Spyware', 1),
(88, 'Gambling Commission', 'Department for Culture, Media and Sport', 'Independent regulator for all commercial gambling in Great Britain (except spread betting). Its core objectives as a regulator are to keep crime out of gambling, to ensure that gambling is conducted fairly and openly and to protect children and other vulnerable people. The Commission is also responsible for issuing codes of practice to the industry, industry guidance to local authorities and advising the Secretary of State on issues related to gambling.', 'http://www.gamblingcommission.gov.uk/', 'hacked', '400,000', 'Macro viruses', 1),
(89, 'Gangmasters and Labour Abuse Authority', 'Home Office', 'The Gangmasters (Licensing) Act 2004 (the GLA Act) created a licensing scheme to regulate the supply of temporary labour to the farming, food processing and shellfish gathering sectors, establishing the Gangmasters Licensing Authority (GLA) to administer that scheme.  The GLA Act aims to protect the most vulnerable workers employed in the regulated sectors by ensuring that those engaged in the supply of workers, or who use workers to provide a service, meet minimum employment and other standards.  The GLA Act also created four criminal offences, arising from the licensing scheme, of operating as an unlicensed gangmaster; entering into arrangements with an unlicensed gangmaster; obstructing GLA officers; and having false documentation.The Immigration Act 2016 determined that GLA should have a broader role in tackling labour exploitation and be renamed as the Gangmasters and Labour Abuse Authority (GLAA).  New powers to investigate labour market offences in England and Wales were given i', 'http://www.gla.gov.uk/', 'inside job', '8,500,000', 'File infectors', 1),
(90, 'Geffrye Museum', 'Department for Culture, Media and Sport', 'To encourage people to learn from and enjoy the Museum’s collections, buildings and gardens, to promote the study of English homes and gardens.', 'http://www.geffrye-museum.org.uk/', 'lost / stolen computer', '100,000', 'System or boot-record infectors', 1),
(91, 'Government Actuary''s Department', 'Government Actuary''s Department', 'Government Actuary''s Department provides actuarial analysis and services to all the main public service pension schemes; policy advice; advice on transfers of employment and certification of private sector organisations’ pension arrangements when staff are transferred; advice to UK Government, and a range of other countries and jurisdictions, on social security arrangements; helps UK Government manage its financial risks; insurance; investment, risk and modelling.', 'www.gov.uk/gad', 'accidentally published', '420,000', 'Polymorphic viruses', 1),
(92, 'Government Internal Audit Agency (GIAA)', 'Her Majesty''s Treasury', 'The Government Internal Audit Agency (GIAA) helps government departments to manage public money effectively by developing better governance, risk management and internal controls. We offer three broad categories of service: (i) Assurance work: This provides an independent and objective evaluation of management activities to give a view on an organisation''s effectiveness in relation to governance, risk management and internal controls. (ii) Advisory work: This helps to identify where the control framework should be improved. (iii) Counter fraud and investigation work: We provide advice and support to customers on counter fraud strategies, fraud risk assessments, and measures to prevent, deter and detect fraud. Where commissioned, we investigate suspicions of internal or supplier fraud or malpractice.', 'https://www.gov.uk/government/organisations/government-internal-audit-agency', 'poor security / hacked', '412,214,295', 'Ransomware', 1),
(93, 'Government Legal Department', 'Government Legal Department', 'The GLD provides legal services to Government departments and other publicly funded bodies in England and Wales. It also administers the estates of people who die intestate with no known kin and the beneficial assets of dissolved companies.', 'https://www.gov.uk/government/organisations/government-legal-department', 'hacked', '8,000,000', 'System or boot-record infectors', 1),
(94, 'Great Britain China Centre', 'Foreign and Commonwealth Office', 'To facilitate effective dialogue with the Chinese government, society and business to provide direct delivery of HMG policy objectives.  Great Britain China Centre promotes mutual understanding between the UK and China and aims to build long-term connections between the decision-makers in the two countries.', 'www.gbcc.org.uk', 'lost / stolen computer', '800,000', 'Polymorphic viruses', 1),
(95, 'Health and Safety Executive', 'Department for Work and Pensions', 'The mission for HSE is the prevention of death, injury and ill health to those at work and those affected by work activities.', 'http://www.hse.gov.uk/', 'hacked', '1,500,000', 'Ransomware', 1),
(96, 'Health Education England', 'Department of Health', 'Health Education England (HEE) exists for one reason only: to help improve the quality of care patients receive. To do this we spend nearly £5bn a year on undergraduate and postgraduate education and training to ensure that the whole health and healthcare sector in England, including the NHS, the independent sector and public health have the most highly qualified new professionals in the world. We are an executive non department public body to the Department of Health, providing system-wide leadership and oversight of workforce planning, education and training across England.', 'http://hee.nhs.uk/', 'hacked', '7,000,000', 'Logic bombs', 1),
(97, 'Health Research Authority', 'Department of Health', 'The HRA was established in December 2011 to protect and promote the interests of patients and the public in health research, and to streamline the regulation of research.  We aim, with partners, to make the UK a great place to do health research, to build confidence and participation in health research, and so improve the nation’s health.', 'http://www.hra.nhs.uk/', 'hacked', '5,000,000', 'Worms', 1),
(98, 'Her Majesty''s Courts and Tribunals Service', 'Ministry of Justice', 'HM Courts and Tribunals Service is responsible for the administration of the criminal, civil and family courts and tribunals in England and Wales and non-devolved tribunals in Scotland and Northern Ireland. It supports a fair, efficient and effective justice system delivered by an independent judiciary.', 'https://www.gov.uk/government/organisations/hm-courts-and-tribunals-service', 'poor security', '500,000', 'Droppers', 1),
(99, 'Her Majesty''s Land Registry', 'Her Majesty''s Land Registry', 'Land Registry registers the ownership of land and property in England and Wales.  We keep and maintain the Land Register, where more than 24 million titles (the evidence of ownership) are documented.', 'http://www.gov.uk/land-registry', 'hacked', '9,000,000', 'Spyware', 1),
(100, 'Her Majesty''s Revenue and Customs', 'Her Majesty''s Revenue and Customs', 'HMRC collects tax and duties from individuals and businesses and supports families and children with Tax credits and  Child Benefit', 'https://www.gov.uk/government/organisations/hm-revenue-customs', 'hacked', '25,000', 'Macro viruses', 1),
(101, 'High Speed 2 Ltd (HS2 Ltd)', 'Department for Transport', 'HS2 Ltd is responsible for developing proposals, promoting and delivering the UK''s high speed rail network.  HS2 Ltd carries out activities in line with the ''HS2 Ltd Framework Document'' and the ''HS2 Development Agreement''.', 'www.gov.uk/HS2', 'inside job', '11,100,000', 'File infectors', 1),
(102, 'Higher Education Funding Council for England', 'Department for Education', 'The Higher Education Funding Council for England promotes and funds high-quality, cost-effective teaching and research in universities and colleges in England, to meet the diverse needs of students, the economy and society.', 'http://www.hefce.ac.uk/', 'hacked', 'unknown', 'System or boot-record infectors', 1),
(103, 'Historic England ', 'Department for Culture, Media and Sport', 'To promote the preservation of ancient monuments, historic buildings and conservation areas and to promote public understanding and enjoyment of the historic environment.', 'http://www.historicengland.org.uk/', 'hacked', '4,200,000', 'Polymorphic viruses', 1),
(104, 'Homes and Communities Agency', 'Department for Communities and Local Government', 'The Homes and Communities Agency is the housing and regeneration agency and social regulator of social housing for England.', 'http://www.homesandcommunities.co.uk/', 'lost / stolen media', '500,000', 'Ransomware', 1),
(105, 'Horniman Public Museum and Public Park Trust', 'Department for Culture, Media and Sport', 'The principal activity of the Trust is the provision of a public, educational Museum and Gardens. Its aim is to use its worldwide collections and the gardens to encourage a wider appreciation of the world, its peoples and their cultures, and its environments.', 'http://www.horniman.ac.uk/', 'lost / stolen media', '1,900,000', 'System or boot-record infectors', 1),
(106, 'Horserace Betting Levy Board', 'Department for Culture, Media and Sport', 'To assess and collect from bookmakers a statutory levy from the British horseracing betting business of bookmakers and the Tote successor company, which it then distributes for the improvement of horseracing and breeds of horses and for the advancement of veterinary science and education.', 'http://www.hblb.org.uk/', 'hacked', '130,000,000', 'Polymorphic viruses', 1),
(107, 'House of Lords Appointments Commission', 'Cabinet Office', 'The Commission''s role is to select new independent members of the House of Lords and to vet party-political nominations put forward by the political parties.', 'http://lordsappointments.independent.gov.uk/', 'lost / stolen media', '2.5GB', 'Ransomware', 1),
(108, 'Housing Ombudsman, The', 'Department for Communities and Local Government', 'To resolve disputes involving members of the Housing Ombudsman Scheme, including making awards of compensation or other remedies when appropriate, as well as to  support effective landlord-tenant dispute resolution by others.', 'http://www.housing-ombudsman.org.uk/', 'lost / stolen media', '200,000', 'Logic bombs', 1),
(109, 'Human Fertilisation and Embryology Authority', 'Department of Health', 'Licensing and inspection of fertility clinics and research projects involving human embryos.  Holder and provider of information for the public, patients, donor conceived people and donors.  Sets policy framework for fertility issues.', 'http://www.hfea.gov.uk/', 'hacked', 'unknown', 'Worms', 1),
(110, 'Human Tissue Authority', 'Department of Health', 'The Human Tissue Authority (HTA) aims to maintain public and professional confidence by ensuring that human tissue and organs are used safely and ethically and with proper consent. We regulate organisations that remove, store and use human tissue and organs for research, medical treatment, post-mortem examination, transplantation, teaching and display in public. We also give approval for organ and bone marrow donations from living people.', 'http://www.hta.gov.uk/', 'hacked', '56,000,000', 'Droppers', 1),
(111, 'Imperial War Museum', 'Department for Culture, Media and Sport', 'The Imperial War Museum is a global authority on conflict and its impact, from the First World War to the present day, in Britain, its former Empire and Commonwealth.', 'http://www.iwm.org.uk/', 'poor security', '283,000', 'Spyware', 1),
(112, 'Independent Advisory Panel on Deaths in Custody', 'Ministry of Justice', 'To act as the primary source of independent advice to ministers and service leaders on measures to reduce the number and rate of deaths in custody.  To consult and engage with stakeholders to collect, analyse and disseminate information about deaths in custody and the lessons that can be learned from them.  To carry out thematic enquiries into areas of concern.  To issue and monitor compliance with guidance on best practice for reducing deaths in custody.  To commission research and make recommendations to ministers for changes in policy or operational practice, which would help to reduce the incidence of death in custody.', 'http://iapdeathsincustody.independent.gov.uk/', 'hacked', '250 locations', 'Macro viruses', 1),
(113, 'Independent Agricultural Appeals Panel', 'Department for Environment, Food and Rural Affairs', 'Panel for appeals lodged with the Rural Payments Agency.', 'https://www.gov.uk/government/organisations/rural-payments-agency', 'hacked', '720,000', 'File infectors', 1),
(114, 'Independent Commission for Aid Impact', 'Department for International Development', 'Independent Commission for Aid Impact (ICAI) is the independent body responsible for scrutinising UK aid. ICAI carries out independent reviews of aid programmes and of issues affecting the delivery of UK aid. ICAI publishes transparent, impartial and objective reports to provide evidence and clear recommendations to support UK Government decision-making and to strengthen the accountability of the aid programme.', 'http://icai.independent.gov.uk/', 'inside job', '6,700', 'System or boot-record infectors', 1),
(115, 'Independent Medical Expert Group', 'Ministry of Defence', 'To provide advice and recommendations to Minister on the Armed Forces Compensation Scheme.', 'https://www.gov.uk/government/organisations/independent-medical-expert-group', 'hacked', '3,000,000', 'Polymorphic viruses', 1),
(116, 'Independent Monitoring Board for the Military Corrective Training Centre', 'Ministry of Defence', 'The Independent Monitoring Board inspect the Military Corrective Training Centre at least twice a year and reports its findings to Ministers. It inspects, amongst other things, the condition of the premises and the treatment, health and welfare of detainees. The arrangements are very similar to those in place for civilian detention facilities in the UK.', 'http://www.army.mod.uk/agc/provost/31946.aspx', 'accidentally published', '1,600,000', 'Ransomware', 1);
INSERT INTO `users_useradd_model` (`id`, `cname`, `dept`, `description`, `website`, `method`, `record`, `attackresult`, `uregid_id`) VALUES
(117, 'Independent Police Complaints Commission', 'Home Office', 'The Independent Police Complaints Commission (IPCC) has a dual purpose to act as an overall guardian ensuring the effectiveness and efficiency of the operation of the whole police complaints system and to also take a role in individual cases.', 'http://www.ipcc.gov.uk/', 'lost / stolen media', '2,600,000', 'System or boot-record infectors', 1),
(118, 'Independent Reconfiguration Panel', 'Department of Health', 'To advise the Secretary of State for Health on contentious proposals for changes to NHS services. To offer support and generic advice to the NHS, local authorities and other interested bodies involved in NHS service reconfiguration.', 'https://www.gov.uk/government/organisations/independent-reconfiguration-panel', 'hacked', '76,000,000', 'Polymorphic viruses', 1),
(119, 'Industrial Injuries Advisory Council', 'Department for Work and Pensions', 'To advise DWP Ministers on matters relating to the Industrial Injuries Scheme and its administration. In particular, which diseases and occupations should give entitlement to Industrial Injuries Disablement Benefit and advising on any draft regulations relating to the Scheme.', 'http://www.gov.uk/iiac', 'hacked', '4,000,000', 'Ransomware', 1),
(120, 'Information Commissioner’s Office', 'Department for Culture, Media and Sport', 'The Information Commissioner’s Office is the UK’s independent authority set up to uphold information rights in the public interest, promoting openness by public bodies and data privacy for individuals.', 'http://www.ico.org.uk/', 'hacked', '125,000', 'Logic bombs', 1),
(121, 'Innovate UK', 'Department for Business, Energy and Industrial Strategy', 'InnovateUK works with people, companies and partner organisations to find and drive the science and technology innovations that will grow the UK economy.', 'https://www.gov.uk/government/organisations/innovate-uk', 'hacked', '1,500,000', 'Worms', 1),
(122, 'Insolvency Rules Committee', 'Ministry of Justice', 'The Insolvency Rules Committee is appointed under section 413 of the Insolvency Act 1986 so that the Lord Chancellor may consult it before making any rules under section 411 (company insolvency rules) or section 412 (individual insolvency rules) of the Act.  The Committee consists of members of the legal and accountancy professionals.', 'http://www.bis.gov.uk/insolvency', 'inside job', '20,000,000', 'Droppers', 1),
(123, 'Insolvency Service', 'Department for Business, Energy and Industrial Strategy', 'IS helps to deliver economic confidence by supporting those in financial distress, tackling financial wrongdoing and maximising returns to creditors.', 'https://www.gov.uk/government/organisations/insolvency-service', 'hacked', '1,000,000', 'Spyware', 1),
(124, 'Intellectual Property Office', 'Department for Business, Energy and Industrial Strategy', 'The IPO is the official UK government body responsible for intellectual property (IP) rights including patents, designs, trade marks and copyright.', 'http://www.ipo.gov.uk/', 'hacked', '8,700,000', 'Macro viruses', 1),
(125, 'Investigatory Powers Tribunal', 'Home Office', 'A statutory body established to consider proceedings bought under  Section 7 of the Human Rights Act 1998 (HRA) against the intelligence services and certain public authorities in respect of their investigatory powers and conduct. To investigate complaints regarding actions by those intelligence services and public authorities who hold powers under the Regulation of Investigatory Powers Act 2000 to ascertain whether they have used those powers lawfully.', 'http://www.ipt-uk.com/', 'hacked', '500 locations', 'File infectors', 1),
(126, 'Joint Nature Conservation Committee', 'Department for Environment, Food and Rural Affairs', 'JNCC is the public body that advises the UK Government and devolved administrations on UK-wide and international nature conservation', 'http://jncc.defra.gov.uk/', 'hacked', '1,000,000', 'System or boot-record infectors', 1),
(127, 'Judicial Appointments Commission', 'Ministry of Justice', 'The Judicial Appointments Commission is an independent commission that selects candidates for judicial office in courts and tribunals in England and Wales, and for some tribunals whose jurisdiction extends to Scotland or Northern Ireland.', 'http://jac.judiciary.gov.uk/', 'lost / stolen media', '130,495', 'Polymorphic viruses', 1),
(128, 'Land Registration Rule Committee', 'Department for Business, Energy and Industrial Strategy', 'The LRRC advises and assists in the making of Land Registration Rules and Land Registration Fee Orders and draft amendments for the Secretary of State under the Land Registration Act 2002.', 'https://www.gov.uk/government/organisations/land-registration-rule-committee', 'accidentally published', '8,000,000', 'Ransomware', 1),
(129, 'Law Commission of England and Wales', 'Ministry of Justice', 'The Law Commission is a statutory independent body. It was created to keep the law under review and, where necessary, to recommend reform of the law to Government. The Law Commission’s aims are to ensure that the law is as fair, modern, simple and as cost-effective as possible. It reviews areas of the law that have become unduly complicated, outdated or unfair. It conducts research and consultation in order to: (1) make systematic recommendations for consideration by Parliament; (2) codify the law; (3) eliminate anomalies; (4) repeal obsolete and unnecessary enactments; and (5) reduce the number of separate statutes. The functions of the Law Commission are set out in two Acts, Law Commissions Act 1965 and Law Commission Act 2009.', 'http://lawcommission.justice.gov.uk/', 'hacked', '50,000,000', 'System or boot-record infectors', 1),
(130, 'Leasehold Advisory Service, The', 'Department for Communities and Local Government', 'To provide front line advice service on residential leasehold law and rights in England & Wales and on Park Homes.', 'http://www.lease-advice.org/', 'hacked', '860,000', 'Polymorphic viruses', 1),
(131, 'Legal Aid Agency', 'Ministry of Justice', 'The Legal Aid Agency''s role is to deliver legal aid efficiently and effectively as part of the justice system.', 'https://www.gov.uk/government/organisations/legal-aid-agency', 'hacked', '10 locations', 'Ransomware', 1),
(132, 'Legal Services Board', 'Ministry of Justice', 'The Legal Services Board (LSB) oversees the regulation of legal services in England and Wales. It does this through its oversight of eleven bodies, the approved regulators, who themselves regulate directly the circa 167,000 lawyers practising throughout the jurisdiction. The LSB does not regulate any legal service provider directly. Working with the approved regulators, the LSB is responsible for ensuring the highest standards of competence, conduct and service in the legal profession both for the benefit of individual consumers and the public generally. The LSB is also responsible for appointing the Office for Legal Complaints, to administer an ombudsman scheme to deal with consumers’ complaints about legal services.', 'http://www.legalservicesboard.org.uk/', 'hacked', '500,000,000', 'Logic bombs', 1),
(133, 'Low Pay Commission', 'Department for Business, Energy and Industrial Strategy', 'The LPC advises the government about the National Living Wage and the National Minimum Wage.', 'https://www.gov.uk/government/organisations/low-pay-commission', 'poor security', '210,000', 'Worms', 1),
(134, 'Marine Management Organisation', 'Department for Environment, Food and Rural Affairs', 'A range of responsibilities, including implementing a new marine planning system and licensing regime; managing UK fishing fleet capacity and UK fisheries quotas; working with Natural England and the Joint Nature Conservation Committee (JNCC) to manage a network of marine protected areas; responding to marine emergencies alongside other agencies.', 'https://www.gov.uk/government/organisations/marine-management-organisation', 'hacked', '160,000,000', 'Droppers', 1),
(135, 'Maritime and Coastguard Agency (MCA)', 'Department for Transport', 'The Maritime and Coastguard Agency (MCA) provides a 24-hour maritime search and rescue service; enforces ship safety, prevents pollution, promotes seafarer health, safety and welfare standards by survey and inspection; registers and certificates ships and seafarers; and manages pollution prevention and response.', 'www.gov.uk/mca', '', '', 'Spyware', 1),
(136, 'Marshall Aid Commemoration Commission', 'Foreign and Commonwealth Office', 'Set up by Marshall Aid Commission Act 1953 to oversee Marshall Scholarships; which are postgraduate Scholarships in the United Kingdom for American students with the potential to excel in their chosen fields of study and future careers.', 'http://www.marshallscholarship.org/', 'hacked', '3,900,000', 'Macro viruses', 1),
(137, 'Medical Research Council', 'Department for Business, Energy and Industrial Strategy', 'The MRC works to improve the health of people in the UK - and around the world - by supporting excellent science, and training the very best scientists.', 'http://www.mrc.ac.uk/', 'lost / stolen media', '102,153', 'File infectors', 1),
(138, 'Medicines and Healthcare products Regulatory Agency', 'Department of Health', 'The Medicines and Healthcare products Regulatory Agency regulates medicines, medical devices and blood components for transfusion in the UK by ensuring they work and are acceptably safe.', 'https://www.gov.uk/government/organisations/medicines-and-healthcare-products-regulatory-agency', 'hacked', '3,000,000', 'System or boot-record infectors', 1),
(139, 'Met Office', 'Department for Business, Energy and Industrial Strategy', 'Met Office, through world leading research, 24/7 operations and technology, provides weather and climate services to government, the Armed Forces, the public, civil aviation, shipping, industry, agriculture and commerce and it also responsible for the UK''s National Severe Weather Warning Service.', 'http://www.metoffice.gov.uk/', 'accidentally published', '163,792', 'Polymorphic viruses', 1),
(140, 'Migration Advisory Committee', 'Home Office', 'Provides independent, evidence based advice to government on migration, particularly shortages in the labour market where migration can contribute.', 'https://www.gov.uk/government/organisations/migration-advisory-committee', 'accidentally published', '6,000,000', 'Ransomware', 1),
