(791, 'Advocate Medical Group', 'healthcare', 'ACCEA administers the National Clinical Excellence Awards Scheme. It advises Ministers on which consultants should receive new National Awards and whether existing award holders have provided satisfactory evidence for renewal.', 'https://stackoverflow.com/questions/43727583/expected-string-or-bytes-like-object/ECSID/getmonlist', 'inside job', '2,000,000', 'Polymorphic viruses', 1),
(792, 'AerServ (subsidiary of InMobi)', 'advertising', 'The Committee is responsible for advising the Secretary of State for Defence on all claims to conscientious objection to further service in the Armed Forces made by officers and other ranks of the Armed Forces whose application for permission to retire or resign their commissions or for discharge have been refused by the Service authorities.', 'https://www.google.co.in/search?q=python+free+online+course+certification&oq=p&aqs/2F4=chrome.0.69i59j69i60l4j69i57.2378j0j7&sourceid=c/tcpoffset/hrome&ie=UTF-8', 'hacked', '5,000,000', 'Ransomware', 1),
(793, 'Affinity Health Plan, Inc.', 'healthcare', 'To recruit, select and recommend to the Lord Chief Justice candidates with the necessary qualities for appointment as Justices of the Peace in England and Wales.', 'http://localhost/phpmyadmin/index.php?token=27a2ceb5cb82727c1f0b63f93f1d0c4f#PMAURL-/x25/NLOM;2:sql.php?db=malware_detection&table=user_malware_recogition_model&server=1&target=&token=27a2ceb5cb82727c1f0b63f93f1d0c4f', 'hacked', 'millions', 'Logic bombs', 1),
